import React from 'react'
//import '../styles/Cards.css';

const Cards = ({ name, id, email }) => {
    return (
        <div className="bg-light-green dib br3 pa3 ma2 grow pointer dim">
            <img className="cent" src={`https://robohash.org/${id}`} alt="robots"/>
            <div className="text">
                <h3>{name}</h3>
                <h4>{email}</h4>
            </div>
        </div>
    )
}

export default Cards;
