import React from 'react'
import '../styles/Cards.css';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import '@splidejs/splide/dist/css/themes/splide-sea-green.min.css';

const Cards = ({ name, id, email }) => {
    return (
        // <div className="cards-wrapper dib br3 pa3 ma2 grow pointer">
        //     <img className="cent" src={`https://robohash.org/${id}?set=set3`} alt="robots"/>
        //     <div className="text">
        //         <h3>{name}</h3>
        //         <h4>{email}</h4>
        //     </div>
        // </div>

        <div>
            <Splide options={{
                rewind: true,
                width: 100,
                gap: '1rem',
            }}>
                <SplideSlide>
                    <img src="https://picsum.photos/305" alt="image1"/>
                </SplideSlide>
                <SplideSlide>
                    <img src="https://picsum.photos/306" alt="image2"/>
                </SplideSlide>
                <SplideSlide>
                    <img src="https://picsum.photos/325" alt="image3"/>
                </SplideSlide>
                <SplideSlide>
                    <img src="https://picsum.photos/315" alt="image4"/>
                </SplideSlide>
            </Splide>
        </div>
    )
}

export default Cards;
