import React from 'react';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import '@splidejs/splide/dist/css/themes/splide-sea-green.min.css';
import '../styles/Splider.css'

const Splider = ({robots}) => {
    const cardSplide = robots.map((robot, i) => {
        return (
            <SplideSlide>
                
            </SplideSlide>
        );
    });

    return(
        <Splide className="cards-slide dib br3 pa3 ma2 pointer" options={{
            rewind: true,
            width: '400px',
            gap: '1rem',
            perPage: 2,
            type: 'loop',
        }}>
            <SplideSlide>
                <img src="https://picsum.photos/305" alt="image1"/>
            </SplideSlide>
            <SplideSlide>
                <img src="https://picsum.photos/306" alt="image2"/>
            </SplideSlide>
            <SplideSlide>
                <img src="https://picsum.photos/325" alt="image3"/>
            </SplideSlide>
            <SplideSlide>
                <img src="https://picsum.photos/315" alt="image4"/>
            </SplideSlide>
        </Splide>
    );
}

export default Splider;
