import React, { Component } from 'react'
import CardList from './components/CardList';
import SearchBox from './components/SearchBox';
import Scroll from './components/Scroll';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import '@splidejs/splide/dist/css/themes/splide-default.min.css';
//import { robots } from './helpers/robots';

class App extends Component {
  constructor(){
    super()
    this.state = {
      robots: [],
      searchField: ''
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(res => {
        return res.json();
      })
      .then(users => {
        this.setState({
          robots: users
        });
      });
    
  }

  onSearchChange = (e) => {
    this.setState({
      searchField: e.target.value 
    });
  }

  render() {
    const filtered = this.state.robots.filter(robot => {
      return robot.name.toLowerCase().includes(this.state.searchField.toLowerCase());
    });

    // Main Render
    return (
      <div className="App tc">
        <h1>RoboBros!</h1>
        <SearchBox searchChange={this.onSearchChange}/>
        <div className="cards-slide bg-light-green dib br3 pa3 ma2 pointer">
            <Splide options={{
                rewind: true,
                width: '400px',
                height: '500px',
                gap: '1rem',
                perPage: 3,
            }}>
                <SplideSlide>
                    <img src="https://picsum.photos/305" alt="image1"/>
                </SplideSlide>
                <SplideSlide>
                    <img src="https://picsum.photos/306" alt="image2"/>
                </SplideSlide>
                <SplideSlide>
                    <img src="https://picsum.photos/325" alt="image3"/>
                </SplideSlide>
                <SplideSlide>
                    <img src="https://picsum.photos/315" alt="image4"/>
                </SplideSlide>
            </Splide>
        </div>
        {/* <CardList robots={filtered}/> */}
      </div>
    )
  }
}

export default App
